import React, { Component } from 'react';
import { StarPeople } from './StarPeople';
import CardList from './CardList';
import SearchBox from './SearchBox';


class App extends Component {
    constructor() {
        super()
        this.state = {
            StarPeople: StarPeople,
            searchfield: ''
        }

    }

    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value})
        const filteredStar = this.state.StarPeople.filter(starPerson => {
            return starPerson.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
        })
        console.log(filteredStar);
    }

  render() {

    return(
        <div className='tc'>
            <h1 className='tc'>Star Wars Universe</h1>
            <SearchBox searchChange={this.onSearchChange}/>
            <CardList StarPeople={this.state.StarPeople}/>
        </div>

    );

  }
}


export default App;
